import axios from "axios";
//import axios from "https://cdnjs.cloudflare.com/ajax/libs/axios/0.19.0/axios.js";
//Vue.use(axios);

export default {
 props: {
  thumbWidthPX: {
    type: String,
    default: "100"
  },
  urlInputBorder: {
    type: String,
    default: "gray"
  },
  urlInputTextColor: {
    type: String,
    default: "black"
  },
  urlInputPlaceholderText: {
    type: String,
    default: "Video link (Youtube or Vimeo)"
  },
  urlInputTextHeightPX: {
    type: String,
    default: "30"
  },
  limitVideoTo: {
    type: Number,
    default: -1
  }
 },
 computed: {
      inputStyle () {
        return 'border: 1px solid ' + this.urlInputBorder +'; color: ' + this.urlInputTextColor + ';' + '; height: ' + this.urlInputTextHeightPX + 'px ;' ;
      },
      thumbStyle () {
        return 'width: ' + this.thumbWidthPX + 'px ;' ;
      },
      showInputTextBox () {
        /* console.log("Videos Length - " + this.videos.length + this.limitVideoTo ); */
        if(this.limitVideoTo == -1 || this.limitVideoTo > this.videos.length)
          return true;
        else
          return false;
      }
  },
  data() {
    return {
      videos: [],
      videoUrl: "",
    };
  },
 methods: {
  onVideoDelete (key) {
    /* console.log("Key - " + key); */
    this.videos.splice(key,1);
    this.$emit("VideoDeleted", {
              position: key,
              videos: this.videos
            });
  },
  onVideoClick(Url, event) {
   /* console.log("Url - " + Url); */
   this.$emit("thumbnailClick", Url);
  },
  addVideo(video) {
    this.videos.push(video);
    /* console.log("Video URL - " + video.Url);
    console.log("Video is valid - " + video.isValid);
    console.log("Video Thumb - " + video.thumbUrl);
    console.log("Type - " + video.type); */

    this.$emit("VideoAdded", {
              videos: this.videos
            });
    this.videoUrl = "";
  },
  processVideoLink(event) {
    var video = {Url: "", isValid: "", thumbUrl: "", type: ""};
    video.Url = event.videoUrl;
    /* console.log(video.Url ); */
    if(!video.isValid)
    {
      video.type = "UNKNOWN";
    if (video.Url == "") {
        // console.log("Do nothing");
        return 0;
      } else {
        video.isValid = false;
        video.thumbUrl = "";
        var notYoutube = false;
        var youtube_video_id = video.Url.match(
          /(youtu[be]*)(\.com|\.be).*(\?v=|\/embed\/|\/)(.{11})$/
        );
        /* 
        var youtube_video_id = this.videoUrl.match(
          /[youtube | youtu]\.[com|be].*(\?v=|\/embed\/|\/)(.{11})/
        );
        */
        //Check if it is youtube video or Vimeo
        if (youtube_video_id) {
          youtube_video_id = youtube_video_id.pop();
          //Verify the length of the youtube viceo id retreived
          if (youtube_video_id.length == 11) {
            var video_thumbnail =
              "https://img.youtube.com/vi/" + youtube_video_id + "/0.jpg";
            video.Url = "https://www.youtube.com/embed/" + youtube_video_id;
            video.isValid = true;
            video.thumbUrl = video_thumbnail;
            video.type = "YOUTUBE";

            this.addVideo(video);
          } else {
            notYoutube = true;
          }
        } else {
          notYoutube = true;
        }
        //To check for vimeo
        if (notYoutube) {
          var VIMEO_BASE_URL = "https://vimeo.com/api/oembed.json?url=";
          var videoTestUrl = video.Url;

          var self = this;


          axios.get(VIMEO_BASE_URL + videoTestUrl).then(
            function(response) {
              video.Url =
                "https://player.vimeo.com/video/" + response.data.video_id;
              video.isValid = true;
              video.thumbUrl = response.data.thumbnail_url_with_play_button;
              video.type = "VIMEO";

              self.addVideo(video);
            },
            function(error) {
              self.$emit("VideoUrlError", {
                videoUrl: video.Url
              });

            }
          ); 
        }



      }
    }
  },
 }
};
